t.script:
            $(document).ready(function(){
    var schema = {
         "last_name_father": {
                            required: true,
                            type: "string",
                            minLength: 1
                        },
                        "first_name": {
                            required: true,
                            type: "string",
                            minLength: 1
                        },
                        "city": {
                            required: true,
                            type: "string",
                            minLength: 1
                        },
                        "birth_date": {
                            required: true,
                            type: "string",
                            minLength: 1
                        },
                        "country_id": {
                            required: true,
                            type: "string",
                            minLength: 1
                        },
                        "address": {
                            required: true,
                            type: "string",
                            minLength: 1
                        },
                        "zip_code": {
                            required: true,
                            type: "string",
                            minLength: 1
                        },
                        "phone": {
                            required: true,
                            type: "string",
                            minLength: 1
                        },
                        "email": {
                            required: true,
                            type: "string",
                            minLength: 1
                        },
                        "rfc": {
                            required: true,
                            type: "string",
                            minLength: 1
                        },

    }

    function validateForm(form, schema) {
        # lets insert a label for the error message inside the element that has the error

        var isValid = true;
        for (var key in schema) {
            var value = form[key].value;
            var rules = schema[key];
            if (rules.required && !value) {
                isValid = false;
                form[key].insertAdjacentHTML('afterend', 'Por favor, llena este campo.');
            }
            if (rules.minLength && value.length < rules.minLength) {
                isValid = false;
                form[key].insertAdjacentHTML('afterend', 'Por favor, llena este campo.');
            }
        }
        return isValid;
    }


    $('#distributor_form').submit(function(e){
        e.preventDefault();
        var form = e.target;
        var isValid = validateForm(form, schema);
        if (isValid) {
            form.submit();
        }

    });

});
